import React, { useState, useRef } from 'react';
import { Button, Modal } from 'react-bootstrap';
import Slider from 'react-slick';
import { ChevronLeft, ChevronRight } from 'react-bootstrap-icons'; // Import Chevron icons
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { Link } from 'react-router-dom';

const Rooms = ({ room }) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const sliderRef = useRef(null); // Create a ref for the Slider component

  const settings = {
    dots: false,
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    arrows: false,
  };

  const nextSlide = () => {
    sliderRef.current.slickNext(); // Call slickNext() method on the Slider ref to move to the next slide
  };

  const prevSlide = () => {
    sliderRef.current.slickPrev(); // Call slickPrev() method on the Slider ref to move to the previous slide
  };

  return (
    <div className="relative h-96 w-96 p-2 rounded-lg shadow-md mx-2 my-8 overflow-hidden">
      <h2 className="text-lg font-bold bg-gray-800 text-white p-2 truncate">{room.name}</h2>
      <div className="relative flex-grow overflow-hidden" onMouseEnter={() => (settings.autoplay = true)} onMouseLeave={() => (settings.autoplay = false)}>
        <Slider {...settings} ref={sliderRef}> {/* Pass the ref to the Slider component */}
          {room.imageurls.map((imageUrl, index) => (
            <img key={index} src={imageUrl} alt={`Room ${index}`} className="w-full h-full object-cover" />
          ))}
        </Slider>
        <button className="absolute top-1/2 transform -translate-y-1/2 left-4 bg-gray-800 text-white rounded-full p-2" onClick={prevSlide}>
          <ChevronLeft size={24} />
        </button>
        <button className="absolute top-1/2 transform -translate-y-1/2 right-4 bg-gray-800 text-white rounded-full p-2" onClick={nextSlide}>
          <ChevronRight size={24} />
        </button>
      </div>
      <div className="p-2 flex justify-between">
        {/* Other JSX */}
      </div>
      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
        centered
        dialogClassName="w-1/3 absolute top-0 left-1/2 transform -translate-x-1/2"
      >
        <Modal.Header closeButton>
          <Modal.Title>{room.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Slider {...settings} initialSlide={currentImageIndex} ref={sliderRef}> {/* Pass the ref to the Slider component */}
            {room.imageurls.map((imageUrl, index) => (
              <img key={index} src={imageUrl} alt={`Room ${index}`} className="w-full h-auto mb-4" />
            ))}
          </Slider>
          <p>{room.description}</p>
        </Modal.Body>
        <Modal.Footer className="flex justify-end">
          {/* Other JSX */}
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Rooms;
