import React, { useState } from 'react';
import { Button, Modal } from 'react-bootstrap';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { Link } from 'react-router-dom';

const Rooms = ({ room }) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const settings = {
    dots: false,
    infinite: true,
    speed: 1000, // Adjust this value to change carousel speed
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false, // Disable autoplay
    arrows: false,
  };

  const nextSlide = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === room.imageurls.length - 1 ? 0 : prevIndex + 1));
  };

  const prevSlide = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? room.imageurls.length - 1 : prevIndex - 1));
  };

  return (
    <div className="relative h-96 w-96 p-2 rounded-lg shadow-md mx-2 my-8 overflow-hidden">
      <h2 className="text-lg font-bold bg-gray-800 text-white p-2 truncate">{room.name}</h2>
      <div className="relative flex-grow overflow-hidden" onMouseEnter={() => settings.autoplay = true} onMouseLeave={() => settings.autoplay = false}>
        <Slider {...settings} ref={slider => (this.slider = slider)}>
          {room.imageurls.map((imageUrl, index) => (
            <img key={index} src={imageUrl} alt={`Room img`} className="w-full h-full object-cover" />
          ))}
        </Slider>
        <button className="absolute top-1/2 transform -translate-y-1/2 left-4 bg-gray-800 text-white rounded-full p-2" onClick={prevSlide}>Prev</button>
        <button className="absolute top-1/2 transform -translate-y-1/2 right-4 bg-gray-800 text-white rounded-full p-2" onClick={nextSlide}>Next</button>
      </div>
      <div className="p-2 flex justify-between">
        <div>
          <p className="text-sm font-bold text-gray-800">Type: <span className="text-gray-600">{room.type}</span></p>
          <p className="text-sm font-bold text-gray-800">Max Count: <span className="text-gray-600">{room.maxcount}</span></p>
          <p className="text-sm font-bold text-gray-800">Rent: <span className="text-gray-600">${room.rentperday}/month</span></p>
        </div>
        <div>
          <Button onClick={handleShow} className="text-sm bg-gray-800 text-white rounded-md px-2 py-1 mt-2 mr-2 focus:outline-none">
            View Details
          </Button>
          <Link to={`/book/${room._id}`}>
            <Button className="text-sm bg-green-500 text-white rounded-md px-2 py-1 mt-2 focus:outline-none">
              Book Now
            </Button>
          </Link>
        </div>
      </div>
      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
        centered
        dialogClassName="w-1/3 absolute top-0 left-1/2 transform -translate-x-1/2" // Modal size is one-third of the screen width
      >
        <Modal.Header closeButton>
          <Modal.Title>{room.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Slider {...settings} initialSlide={currentImageIndex}>
            {room.imageurls.map((imageUrl, index) => (
              <img key={index} src={imageUrl} alt={`Room ${index}`} className="w-full h-auto mb-4" />
            ))}
          </Slider>
          <p>{room.description}</p>
        </Modal.Body>
        <Modal.Footer className="flex justify-end">
          <Button variant="secondary" className='bg-blue-500 text-white px-4 py-2' onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Rooms;
