import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Loading from './Loading';
import { useNavigate } from 'react-router-dom';
const RegisterForm = () => {

  const navigate=useNavigate();
  const [loading,setLoading]=useState(false);
  const [error,setError]=useState(false)
  const [success,setsuccess]=useState(false);

  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    password: '',
    cpassword: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  async function handleSubmit(e) {
    e.preventDefault();
    const {name,phone,password,cpassword}=formData;
    const user={
        name,
        phone,
        password,
        cpassword
    }
    try{
      setLoading(true);
      const data=(await axios.post("/api/users/register",user)).data;
      setsuccess(true);
      console.log(data);
    }
    catch(error){
        console.log(error.messsage);
        setLoading(false);
        setError(true);
        console.log(error);
    }
    setLoading(false);
    console.log(formData);
  };
  
 useEffect(()=>{
   if(success){
    navigate("/login");
   }
 },[success])
  return (
      <div className="flex justify-center items-center h-screen bg-gradient-to-br from-purple-600 to-pink-600">
      {loading && <Loading/>}
      <form className="bg-white shadow-lg rounded-lg p-8 max-w-md w-full" onSubmit={handleSubmit}>
        <h2 className="text-2xl font-semibold mb-4 text-center">Sign Up</h2>
        <div className="mb-4">
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Name"
            className="w-full px-3 py-2 border rounded-md focus:outline-none focus:border-purple-500"
          />
        </div>
        <div className="mb-4">
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            placeholder="Phone Number"
            className="w-full px-3 py-2 border rounded-md focus:outline-none focus:border-purple-500"
          />
        </div>
        <div className="mb-4">
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="Password"
            className="w-full px-3 py-2 border rounded-md focus:outline-none focus:border-purple-500"
          />
        </div>
        <div className="mb-6">
          <input
            type="password"
            name="cpassword"
            value={formData.cpassword}
            onChange={handleChange}
            placeholder="Confirm Password"
            className="w-full px-3 py-2 border rounded-md focus:outline-none focus:border-purple-500"
          />
        </div>
        <button
          type="submit"
          className="w-full bg-purple-600 text-white py-2 rounded-md hover:bg-purple-700 focus:outline-none focus:bg-purple-700"
        >
          Sign Up
        </button>
      </form>
    </div>
  );
};

export default RegisterForm;
