import React, { useState, useEffect } from 'react';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/solid';
import { Button, Modal } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const Rooms = ({ room }) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    // Automatically transition to the next image every 3 seconds
    const intervalId = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % room.imageurls.length);
    }, 3000);

    // Clear the interval on component unmount
    return () => clearInterval(intervalId);
  }, [room.imageurls.length]);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % room.imageurls.length);
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? room.imageurls.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="relative h-96 w-96 p-2 rounded-lg shadow-lg mx-2 my-8 overflow-hidden">
      <h2 className="text-lg font-bold bg-gray-800 text-white p-2 truncate absolute top-0 left-0 right-0 z-10">{room.name}</h2>
      <div className="absolute inset-0 flex justify-between items-center">
        <button onClick={handlePrevImage} className="absolute left-0 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full focus:outline-none z-10">
          <ChevronLeftIcon className="h-6 w-6" />
        </button>
        <button onClick={handleNextImage} className="absolute right-0 top-1/2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full focus:outline-none z-10">
          <ChevronRightIcon className="h-6 w-6" />
        </button>
        {room.imageurls.map((imageUrl, index) => (
          <img
            key={index}
            src={imageUrl}
            alt="Room"
            className={`w-full h-full object-cover absolute transition-opacity duration-500 ${
              index === currentImageIndex ? 'opacity-100' : 'opacity-0'
            }`}
          />
        ))}
      </div>
      <div className="p-2 flex justify-between">
        <div>
          <p className="text-sm font-bold text-gray-800">Type: <span className="text-gray-600">{room.type}</span></p>
          <p className="text-sm font-bold text-gray-800">Max Count: <span className="text-gray-600">{room.maxcount}</span></p>
          <p className="text-sm font-bold text-gray-800">Rent: <span className="text-gray-600">₹{room.rentperday}/month</span></p>
        </div>
        <div>
          <Button onClick={handleShow} className="text-sm bg-gray-800 text-white rounded-md px-2 py-1 mt-2 mr-2 focus:outline-none">
            View Details
          </Button>
          <Link to={`/book/${room._id}`}>
            <Button className="text-sm bg-green-500 text-white rounded-md px-2 py-1 mt-2 focus:outline-none">
              Book Now
            </Button>
          </Link>
        </div>
      </div>
      <div>
        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <span></span>
            <Modal.Title>{room.description}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <img src={room.imageurls[0]} alt="image unavailable" />
          </Modal.Body>
          <Modal.Footer>
            <Button variant="" className='bg-blue-700 text-white' onClick={handleClose}>
              Close
            </Button>
            <Button variant="" className="bg-green-700 text-white" onClick={handleClose}>
              Ask from us
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    </div>
  );
};

export default Rooms;
