import React, { useState } from 'react';
import { Container, Nav, Navbar } from 'react-bootstrap';
import { useLocation, Link } from 'react-router-dom';
// import logo from './logo.png'; // Assuming you have a logo file named logo.png in the same directory

function NavbarComponent() {
  const location = useLocation();
  const [darkTheme, setDarkTheme] = useState(false);

  const userName=JSON.parse(localStorage.getItem("username"));
  
  const handleThemeToggle = (e) => {
    const darkModeStatus=e.currentTarget.checked;
    console.log(darkModeStatus)
    if (!darkModeStatus) {
      document.body.classList.remove('dark');
      document.body.classList.add('light');
      setDarkTheme(false);

    } else {
      document.body.classList.remove('light');
      document.body.classList.add('dark');
      setDarkTheme(true);
    }
  };

  return (
    <>
      <Navbar className='bg-gray-800'>
        <Container className="justify-between flex px-2 py-2">
          <Navbar.Brand href="/home" className='text-white flex'>
            <img 
              src="https://i.pinimg.com/originals/f4/fa/ec/f4faec1798f199132d27ed903701818b.png"
              className="d-inline-block align-top mr-2 h-10 w-10 rounded-lg"
              alt="PgDekho Logo"
            />
            PgDekho.com
          </Navbar.Brand>
          {location.pathname === '/home' && (
            <Nav className="flex justify-between">
              <input type="text" placeholder="Search..." className="form-control mr-2 rounded-lg w-auto" />
            </Nav>
          )}
          {location.pathname === '/home' && (
            <Nav className="align-items-center">
             <label className="radio-container mr-4 text-white">
               <input type="checkBox" checked={darkTheme===true} onChange={handleThemeToggle} />
               <span className="radio-checkmark">Toggle</span>
             </label>
             <div className="flex justify-between items-center py-2 ml-auto relative">
